package statement;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  for(int r=1;r<=4;r++)
  {
	  for(int c=1;c<=r;c++)
       {
		  System.out.print(c);
       } 
	  System.out.println();
  } 
  for(int i=1;i<=2;i++) {
	  System.out.print(i);
  }for(int a=5;a>=3;a--)
  {
	  System.out.print(a);
  }
  
}
}
